/**
 * 02_Format_ForEmbedding for ShortTermPrograms_QA
 * Mode: Run Once for Each Item
 *
 * 01_Get_ManualDB_Records の出力
 *  - id
 *  - name
 *  - url
 *  - property_answer
 *  - property_program
 *  - property_category
 *  - property_keyword
 *  - property_updated_at
 *  - property_source_url
 *  - property_source_file
 * を前提に、Embedding & Pinecone 用のJSONに整形する。
 */

console.log("=".repeat(60));
console.log("【02_Format_ForEmbedding 実行開始】");
console.log("Input keys:", Object.keys($json));

/**
 * ヘルパー：配列 or オブジェクト or 文字列を「配列<string>」にそろえる
 */
function normalizeToStringArray(value) {
  if (Array.isArray(value)) {
    return value.map(v => String(v).trim()).filter(v => v);
  }
  if (value && typeof value === "object") {
    return Object.values(value)
      .map(v => String(v).trim())
      .filter(v => v);
  }
  if (typeof value === "string") {
    const t = value.trim();
    return t ? [t] : [];
  }
  return [];
}

/**
 * 1. 各フィールドの取り出し
 */

// Notion ページID
const pageId = ($json.id || "").trim();

// Question（タイトル）: name を優先
const question =
  (typeof $json.name === "string" && $json.name.trim()) ||
  ($json.property_question || "").trim() ||
  "";

// Answer
const answer = ($json.property_answer || $json.Answer || "").toString().trim();

// Program
const program = ($json.property_program || $json.Program || "").toString().trim();

// Category（文字列 or 配列想定）
let category = "";
if (Array.isArray($json.property_category)) {
  category = $json.property_category.join(", ");
} else if (
  $json.property_category &&
  typeof $json.property_category === "object"
) {
  category = Object.values($json.property_category)
    .map(v => String(v).trim())
    .join(", ");
} else if (typeof $json.property_category === "string") {
  category = $json.property_category.trim();
}

// Keyword（multi-select）
const keywords = normalizeToStringArray($json.property_keyword);

// Status（あれば）
const status = ($json.property_status || $json.Status || "").toString().trim();

// SourceURL（Notionの url もフォールバック）
const sourceURL =
  ($json.property_source_url || "").toString().trim() ||
  ($json.url || "").toString().trim();

// UpdatedAt（日付文字列 → ISO化を試みる）
let updatedAtRaw =
  ($json.property_updated_at || $json.UpdatedAt || "").toString().trim();
let updatedAt = "";
if (updatedAtRaw) {
  try {
    updatedAt = new Date(updatedAtRaw).toISOString();
  } catch (e) {
    updatedAt = updatedAtRaw; // そのまま保持
  }
}

// SourceFile
const sourceFile =
  ($json.property_source_file || $json._source_file || "").toString().trim();

// ログ出力
console.log("pageId:", pageId);
console.log("question:", question);
console.log("answer(length):", answer.length);
console.log("program:", program);
console.log("category:", category);
console.log("keywords:", keywords);
console.log("status:", status);
console.log("sourceURL:", sourceURL);
console.log("updatedAt:", updatedAt);
console.log("sourceFile:", sourceFile);

/**
 * 2. 簡易バリデーション
 */
const errors = [];
if (!pageId) errors.push("pageId が空です");
if (!question) errors.push("Question(タイトル) が空です");
if (!answer) errors.push("Answer が空です");

if (errors.length) {
  console.log("❌ バリデーションエラー:", errors.join(" | "));
  throw new Error(errors.join(" | "));
}

/**
 * 3. Embedding 用テキストの生成
 *    形式: Q: ... / A: ...
 */
let textForEmbedding = `Q: ${question}\nA: ${answer}`;

// 長すぎる場合は 2000 文字でカット
if (textForEmbedding.length > 2000) {
  textForEmbedding = textForEmbedding.substring(0, 2000);
}

console.log("textForEmbedding preview:");
console.log(textForEmbedding.substring(0, 200));
console.log("length:", textForEmbedding.length);

/**
 * 4. 返り値
 *    03_OpenAI_Embedding → 03a_Merge_ForUpsert → 04_Pinecone_Upsert_Code が
 *    期待しているフィールド名に合わせて返す
 */
const output = {
  json: {
    pageId,
    pageTitle: question,   // 04_Pinecone_Upsert_Code が参照
    question,
    answer,
    program,
    category,
    keywords,
    status,
    sourceURL,
    updatedAt,
    sourceFile,
    textForEmbedding
  }
};

console.log("✅ 02_Format_ForEmbedding 実行完了");
console.log("=".repeat(60));

return output;

// 04_Pinecone_Upsert_Code（バッチ upsert 版）

// すべての item を取得
const items = $input.all();

// バッチサイズ（1回で送るベクトル数）
// 50?100 くらいなら十分速くて安全
const BATCH_SIZE = 50;

// Pinecone の設定（URL は今使っているものに合わせてください）
const PINECONE_URL = 'https://rsjp-rwjp-v2-s86bwtk.svc.aped-4627-b74a.pinecone.io/vectors/upsert';

// ★ここは「今のコードの headers 部分」をそのまま貼り付けてください★
const HEADERS = {
  'Api-Key': 'pcsk_2DYVK1_3uibMjeoRKcZCL3hra2hPNsmfxHj1BeU2P6b2PnEBAp6bvsex2a9zJPBZQPrh26',
  'Content-Type': 'application/json'
};

// items → vectors への変換関数
function buildVectors(fromItems) {
  return fromItems.map(item => {
    const j = item.json;

    const pageId          = j.pageId;
    const embedding       = j.data[0].embedding;
    const pageTitle       = j.pageTitle || '';
    const sourceURL       = j.sourceURL || '';
    const updatedAt       = j.updatedAt || new Date().toISOString();
    const textForEmbedding = j.textForEmbedding || '';
    const program         = j.program || '';
    const category        = j.category || '';
    const keywords        = j.keywords || [];
    const status          = j.status || '';
    const sourceFile      = j.sourceFile || '';

    return {
      id: pageId,
      values: embedding,
      metadata: {
        pageId,
        pageTitle,
        sourceURL,
        updatedAt,
        textForEmbedding,
        program,
        category,
        keywords,
        status,
        sourceFile,
      },
    };
  });
}

const allResponses = [];

// BATCH_SIZE ごとに分割して upsert
for (let i = 0; i < items.length; i += BATCH_SIZE) {
  const batchItems = items.slice(i, i + BATCH_SIZE);
  const vectors = buildVectors(batchItems);

  const payload = {
    vectors,
    namespace: 'rsjp_rsjpfaq_v1',   // ここは今まで通りの namespace
  };

  const res = await this.helpers.httpRequest.call(this, {
    method: 'POST',
    url: PINECONE_URL,
    headers: HEADERS,
    body: payload,
    json: true,
  });

  allResponses.push(res);
}

// まとめて 1件だけ返す
return [
  {
    json: {
      success: true,
      upsertedCount: items.length,
      pineconeResponses: allResponses,
    },
  },
];
